                                    new_used.add(winner)
                                    if winner == current_champion:
                                        result = solve(mid + 1, end, phase_index + 1, winner, new_used, memo)
                                    else:
                                        result = solve(mid + 1, end, phase_index + 1, current_champion, new_used, memo)
                                    break

        memo[key] = result
        return result

    winning_numbers = []
    for x in range(1, total_teams + 1):
        memo = {}
        if solve(0, total_teams - 1, 0, x, {x}, memo):
            winning_numbers.append(x)

