import sys
import math
from collections import deque

def readints():
    return list(map(int, sys.stdin.readline().split()))

def sign(x):
    return 1 if x > 0 else -1

def cross(a, b):
    return a[0] * b[1] - a[1] * b[0]

def intersect(line1, line2):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    det = a1 * b2 - a2 * b1
    if det == 0:
        return None
    x = (b1 * c2 - b2 * c1) / det
    y = (a2 * c1 - a1 * c2) / det
    return (x, y)

def is_point_on_line(x, y, a, b, c):
    return abs(a * x + b * y + c) < 1e-8

def get_half_plane(line, point):
    a, b, c = line
    val = a * point[0] + b * point[1] + c
    return sign(val)

def bfs(start, end, lines):
    n = len(lines)
    visited = [False] * n
    queue = deque()
    queue.append((start, 0))
    visited[0] = True
    while queue:
        point, steps = queue.popleft()
        for i in range(n):
            if not visited[i]:
                if get_half_plane(lines[i], point) != get_half_plane(lines[i], end):
                    visited[i] = True
                    queue.append((point, steps + 1))
                    if steps + 1 == 1:
                        return 1
    return -1

def main():
    x1, y1 = readints()
    x2, y2 = readints()
    n = int(sys.stdin.readline())
    lines = []
    for _ in range(n):
        a, b, c = map(int, sys.stdin.readline().split())
        lines.append((a, b, c))

    start = (x1, y1)
    end = (x2, y2)

    # Check if start and end are in the same block
    if all(get_half_plane(line, start) == get_half_plane(line, end) for line in lines):
        print(0)
        return

    # BFS to find minimum steps
    result = bfs(start, end, lines)
    print(result)

if __name__ == "__main__":
    main()

