    sorted_nodes.sort()

    parent = [i for i in range(n+1)]
    rank = [0] * (n+1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        rx = find(x)
        ry = find(y)
        if rx == ry:
            return False
        if rank[rx] < rank[ry]:
            parent[rx] = ry
        elif rank[rx] > rank[ry]:
            parent[ry] = rx
        else:
            parent[ry] = rx
            rank[rx] += 1
        return True

    s = set()
    for i in range(n, n-k, -1):
        s.add(i)

    nodes_set = set(range(1, n+1))
    current_set = nodes_set - s

    for node in sorted_nodes[:k]:
