            continue
        current_set.add(node)
        s.remove(node)
        for edge in edges:
            a, b = edge
            if (a == node or b == node) and a in current_set and b in current_set:
                break
        else:
            for edge in edges:
                a, b = edge
                if a == node or b == node:
                    if a in current_set and b not in current_set:
