def main():
    data = sys.stdin.read().split()
    if not data:
        return

    it = iter(data)
    n = int(next(it)); k = int(next(it))
    edges = []
    graph = [[] for _ in range(n+1)]

    for _ in range(n-1):
        a = int(next(it)); b = int(next(it))
        graph[a].append(b)
        graph[b].append(a)

    top_set = set(range(n-k+1, n+1))
    removed_set = set(range(1, n-k+1))

    def bfs(start, target_set):
        visited = [False] * (n+1)
        q = deque([start])
        visited[start] = True
        count = 1 if start in target_set else 0
        while q:
            node = q.popleft()
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    if neighbor in target_set:
                        count += 1
                    q.append(neighbor)
        return count == len(target_set)

    if bfs(next(iter(top_set)), top_set):
        removed_list = sorted(list(removed_set))
        print(" ".join(map(str, removed_list)))
        return

    pq_remove = []
    pq_add = []
    for i in range(1, n+1):
        if i in top_set:
            heapq.heappush(pq_remove, i)
        else:
            heapq.heappush(pq_add, i)

    while True:
        r_node = heapq.heappop(pq_remove)
        a_node = heapq.heappop(pq_add)
        new_top_set = top_set - {r_node} | {a_node}
        if bfs(next(iter(new_top_set)), new_top_set):
            removed_set = (removed_set - {a_node}) | {r_node}
            break
        heapq.heappush(pq_remove, r_node)
        heapq.heappush(pq_add, a_node)

    removed_list = sorted(list(removed_set))
    print(" ".join(map(str, removed_list)))

