    inv_fact[k_val] = pow(fact[k_val], mod-2, mod)
    for i in range(k_val, 0, -1):
        inv_fact[i-1] = inv_fact[i] * i % mod

    def nCr(n_val, r_val):
        if r_val < 0 or r_val > n_val:
            return 0
        return fact[n_val] * inv_fact[r_val] % mod * inv_fact[n_val - r_val] % mod

    def sum_combinations(total, min_val):
        res = 0
        for x in range(min_val, total + 1):
            res = (res + nCr(total, x)) % mod
        return res

    def count_assignments(max_size):
        if max_size < 0:
            return 0
        total = (1 << n) - 1
        if max_size == 0:
            return 1
        ways = 0
        for root_val in range(1, max_size + 1):
            ways_root = nCr(k_val, root_val)
            sub_ways = count_assignments(max_size - 1)
            ways = (ways + ways_root * sub_ways) % mod
        return ways

