        s = stack.pop()
        if len(s) == n:
            continue
        left_val = dfs(s + '0')
        right_val = dfs(s + '1')
        total_here = min(k, left_val + right_val)
        if len(s) == 1:
            T += total_here
        else:
            parent_val = dfs(s)
            if parent_val < total_here:
                T += parent_val
            else:
                T += total_here

    T %= MOD
    if T == f_val:
        print(pow(k, total_leaves, MOD))
    else:
        print(0)

