def main():
    import sys
    data = sys.stdin.read().split()
    n = int(data[0])
    k_val = int(data[1])
    f_val = int(data[2])

    total_nodes = (1 << (n + 1)) - 3
    total_leaves = 1 << n

    def comb(n, r):
        if r < 0 or r > n:
            return 0
        if r == 0 or r == n:
            return 1
        num = 1
        den = 1
        for i in range(1, r + 1):
            num = num * (n - i + 1) % MOD
            den = den * i % MOD
        return num * pow(den, MOD - 2, MOD) % MOD

    def solve_tree(depth, total_children, max_constraint, min_required):
        if depth == n:
            return comb(max_constraint, min_required)

        total_ways = 0
        for left in range(0, max_constraint + 1):
            right = total_children - left
            if right < 0 or right > max_constraint:
                continue
            ways_left = solve_tree(depth + 1, total_children, left, min_required)
            ways_right = solve_tree(depth + 1, total_children, right, min_required)
            total_ways = (total_ways + ways_left * ways_right) % MOD
        return total_ways

    def count_assignments(max_multiset_size, n, k_val):
        total = 0
        for x in range(0, max_multiset_size + 1):
            term1 = pow(k_val + 1, (1 << n) - 1 - x, MOD)
            term2 = pow(k_val + 1, x, MOD)
            total = (total + term1 * term2) % MOD
        return total

    if n == 1:
        total_leaves = 2
        total_assignments = 0
        for c0 in range(0, k_val + 1):
            for c1 in range(0, k_val + 1):
                total_size = c0 + c1
                if total_size == f_val:
                    total_assignments = (total_assignments + 1) % MOD
        print(total_assignments)
        return

    dp = [0] * (n + 1)
    dp[0] = 1
    for i in range(1, n + 1):
        dp[i] = 2 * dp[i - 1]

    min_required = f_val
    total_ways_tree = solve_tree(0, dp[n - 1], k_val, min_required)

    total_ways_all = 0
    for i in range(0, total_nodes + 1):
        if i <= f_val:
            total_ways_all = (total_ways_all + pow(k_val + 1, i, MOD)) % MOD

    total_ways_all = (total_ways_all * pow(k_val + 1, total_nodes - f_val - 1, MOD)) % MOD

    ans = (total_ways_all - total_ways_tree) % MOD
    if ans < 0:
        ans += MOD
    print(ans)

