            seen = set()
            for num in numbers:
                if num in seen:
                    continue
                seq = []
                current = num
                while current <= num + 2 and current <= 9:
                    if f"{current}{suit}" in count_tile:
                        seq.append(f"{current}{suit}")
                    current += 1
                if len(seq) == 3:
                    has_shuntsu = True
                    break
