import sys
from collections import Counter

def is_shuntsu(tiles):
    if len(tiles) != 3:
        return False
    tiles = sorted(tiles)
    suit = tiles[0][1]
    if any(tile[1] != suit for tile in tiles):
        return False
    numbers = [int(tile[0]) for tile in tiles]
    return numbers == [numbers[0], numbers[0]+1, numbers[0]+2]

def is_koutsu(tiles):
    if len(tiles) != 3:
        return False
    count = Counter(tiles)
    for tile, cnt in count.items():
        if cnt >= 3:
            return True
    return False

def can_form_mentsu(tiles):
    count = Counter(tiles)
    for tile, cnt in count.items():
        if cnt >= 3:
            return True
    tiles_sorted = sorted(tiles)
    for i in range(len(tiles_sorted) - 2):
        if tiles_sorted[i][1] == tiles_sorted[i+1][1] == tiles_sorted[i+2][1]:
            a, b, c = int(tiles_sorted[i][0]), int(tiles_sorted[i+1][0]), int(tiles_sorted[i+2][0])
            if abs(a - b) == 1 and abs(b - c) == 1:
                return True
    return False

def min_extra_tiles(tiles):
    if can_form_mentsu(tiles):
        return 0
    for extra in range(1, 4):  # At most 3 extra tiles needed
        for i in range(3):
            for j in range(10):
                for k in ['m', 'p', 's']:
                    new_tiles = tiles + [f"{j}{k}"]
                    if can_form_mentsu(new_tiles):
                        return extra
    return 4

def main():
    tiles = sys.stdin.read().strip().split()
    print(min_extra_tiles(tiles))

