def find_possible_message(t):
    n = len(t)
    for i in range(1, n - 1):
        # Check if the first part and the second part can be merged
        # by overlapping a prefix of the second part with a suffix of the first part
        prefix_len = i
        suffix_len = n - i
        if prefix_len == 0 or suffix_len == 0:
            continue
        if t[:prefix_len] == t[n - suffix_len:]:
            # Check if the overlapping is valid (not full overlap)
            if prefix_len != suffix_len:
                return t[:prefix_len]
    return "NO"

