def main():
    n = int(input().strip())
    arr = list(map(int, input().split()))

    if n == 0:
        print(0)
        return

    if min(arr) == max(arr):
        print(n)
        return

    total_ones = sum(1 for x in arr if x == 1)
    total_twos = n - total_ones

    if total_ones == 0 or total_twos == 0:
        print(n)
        return

    left_ones = [0] * n
    right_twos = [0] * n

    for i in range(n):
        if arr[i] == 1:
            left_ones[i] = 1 + (left_ones[i-1] if i > 0 else 0)
        else:
            left_ones[i] = 0

    for i in range(n-1, -1, -1):
        if arr[i] == 2:
            right_twos[i] = 1 + (right_twos[i+1] if i < n-1 else 0)
        else:
            right_twos[i] = 0

    best = 0
    for i in range(n):
        if arr[i] == 1:
            left_count = left_ones[i]
            right_count = 0
            for j in range(i+1, n):
                if arr[j] == 2:
                    right_count += 1
                else:
                    break
            candidate = left_count + right_count
            if candidate > best:
                best = candidate

        else:
            right_count = 0
            for j in range(i, -1, -1):
                if arr[j] == 2:
                    right_count += 1
                else:
                    break
            left_count = 0
            for j in range(i+1, n):
                if arr[j] == 1:
