def main():
    import sys
    input = sys.stdin.read
    n, m, g = map(int, input().split())

    total_length = n + m

    # The number of operations needed to reduce the string to length 1
    # Each operation reduces the length by 1
    # So we need (total_length - 1) operations
    # Let's simulate the process of reducing the string
    # We can track the number of 0s and 1s at each step

    # Start with n zeros and m ones
    zeros = n
    ones = m

    for _ in range(total_length - 1):
        if zeros >= 2:
            zeros -= 1
            ones += 0
        elif ones >= 2:
            ones -= 1
            zeros += 0
        else:
            break

    # After all possible reductions, we have one character left
    final_char = '0' if zeros else '1'

    if final_char == str(g):
        # We need to count the number of beautiful strings that reduce to g
        # This is equivalent to counting the number of ways to arrange n 0s and m 1s
        # such that the final character after reduction is g
        # To do this, we need to simulate the process again and track the number of ways

        # We'll use dynamic programming to count the number of ways
        # dp[i][j] = number of ways to have i 0s and j 1s
        # and end up with a string that reduces to g

        dp = [[0] * (m + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(n + 1):
            for j in range(m + 1):
                if i + j == 0:
                    continue
                if i >= 2:
                    dp[i][j] = (dp[i][j] + dp[i - 1][j]) % MOD
                if j >= 2:
                    dp[i][j] = (dp[i][j] + dp[i][j - 1]) % MOD

        # Now, we need to find how many ways to get to a string that reduces to g
        # We can simulate the reduction again and track the final character
        # We'll use memoization to avoid recomputation

        from functools import lru_cache

        @lru_cache(maxsize=None)
        def reduce(i, j):
            if i + j == 1:
                return '0' if i else '1'
            if i >= 2:
                return reduce(i - 1, j)
            if j >= 2:
                return reduce(i, j - 1)

        final_char = reduce(n, m)

        if final_char == str(g):
            # Now, we need to count the number of strings that reduce to g
            # We can simulate the process again and track the number of ways to get to each state
            # That's what we did in dp above

            # We can now compute the number of ways to reach a string that reduces to g
            # But we need to track the final character after reduction
            # So we'll create another DP table that tracks the final character

            # dp_final[i][j][c] = number of ways to have i 0s and j 1s that reduce to c
            # c is 0 or 1

            dp_final = [[[0] * 2 for _ in range(m + 1)] for _ in range(n + 1)]
            dp_final[0][0][0] = 1
            dp_final[0][0][1] = 0

            for i in range(n + 1):
                for j in range(m + 1):
                    if i + j == 0:
                        continue
                    if i >= 2:
                        for c in range(2):
                            dp_final[i][j][c] = (dp_final[i][j][c] + dp_final[i - 1][j][c]) % MOD
                    if j >= 2:
                        for c in range(2):
                            dp_final[i][j][c] = (dp_final[i][j][c] + dp_final[i][j - 1][c]) % MOD

            # Now, we need to simulate the reduction again and track the final character
            # We can use memoization again to find the final character for each (i, j)

            @lru_cache(maxsize=None)
            def reduce_char(i, j):
                if i + j == 1:
                    return '0' if i else '1'
                if i >= 2:
                    return reduce_char(i - 1, j)
                if j >= 2:
                    return reduce_char(i, j - 1)

            final_char = reduce_char(n, m)

            if final_char == str(g):
                # The answer is the number of ways to get to a string that reduces to g
                result = dp_final[n][m][g]
            else:
                result = 0
        else:
            result = 0
    else:
        result = 0

    print(result % MOD)

