    def dp(i, h, m):
        if i == total_digits:
            return 0
        res = -1
        if h < n:
            val1 = dp(i+1, h+1, m)
            total1 = int(A[i]) * exp_list[h] + val1
        else:
            total1 = -1
        if m < n:
            val2 = dp(i+1, h, m+1)
            total2 = int(A[i]) * exp_list[m] + val2
        else:
            total2 = -1

        if total1 == -1 and total2 == -1:
            return -1
        elif total1 == -1:
            return total2
        elif total2 == -1:
            return total1
        else:
            return max(total1, total2)

