def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    A = data[1]

    from functools import lru_cache

    @lru_cache(maxsize=None)
    def dfs(pos, h_moves, m_moves):
        if h_moves == n and m_moves == n:
            return 0
        total = 0
        digit = A[pos]
        if h_moves < n:
            h_val = int(digit) * (10 ** (n - h_moves - 1))
            res_h = h_val + dfs(pos + 1, h_moves + 1, m_moves)
            total = max(total, res_h)
        if m_moves < n:
            m_val = int(digit) * (10 ** (n - m_moves - 1))
            res_m = m_val + dfs(pos + 1, h_moves, m_moves + 1)
            total = max(total, res_m)
        return total

    # Now find the path that gives maximum value
    result = []
    pos = 0
    h_moves = 0
    m_moves = 0

    def backtrack(pos, h_moves, m_moves, path):
        if h_moves == n and m_moves == n:
            return path
        digit = A[pos]
        if h_moves < n:
            res_h = int(digit) * (10 ** (n - h_moves - 1)) + dfs(pos + 1, h_moves + 1, m_moves)
            res_m = int(digit) * (10 ** (n - m_moves - 1)) + dfs(pos + 1, h_moves, m_moves + 1)
            if res_h >= res_m:
                return backtrack(pos + 1, h_moves + 1, m_moves, path + 'H')
        if m_moves < n:
            res_m = int(digit) * (10 ** (n - m_moves - 1)) + dfs(pos + 1, h_moves, m_moves + 1)
            res_h = int(digit) * (10 ** (n - h_moves - 1)) + dfs(pos + 1, h_moves + 1, m_moves)
            if res_m >= res_h:
                return backtrack(pos + 1, h_moves, m_moves + 1, path + 'M')
        return path

    result = backtrack(0, 0, 0, "")
    print(result)

