    for i in range(1, p_val + 1):
        dp[i] = float('inf')

    for k in range(1, n + 1):
        t_val = k * (k - 1) // 2
        if t_val > p_val:
            continue
        for j in range(t_val, p_val + 1):
            if dp[j - t_val] != float('inf'):
                if dp[j] > dp[j - t_val] + k:
                    dp[j] = dp[j - t_val] + k

