    edges_in_SCCs = 0
    temp = min_nodes
    k = 1
    while temp > 0:
        t_val = k * (k - 1) // 2
        if t_val <= temp:
            edges_in_SCCs += t_val
            temp -= t_val
            k += 1
        else:
            k += 1

    unidirectional_pairs = total_possible_edges - edges_in_SCCs

    print(f"{min_nodes} {unidirectional_pairs}")

