def main():
    import sys
    input = sys.stdin.read
    N = int(input())

    if N == 1:
        print(-1)
        return

    # For N >= 2, we can construct a board where rook pays less than queen
    # We use a spiral pattern to make the rook move more efficiently than the queen

    board = [[0] * N for _ in range(N)]
    num = 1
    top, bottom, left, right = 0, N-1, 0, N-1

    while top <= bottom and left <= right:
        for i in range(left, right + 1):
            board[top][i] = num
            num += 1
        top += 1

        for i in range(top, bottom + 1):
            board[i][right] = num
            num += 1
        right -= 1

        if top <= bottom:
            for i in range(right, left - 1, -1):
                board[bottom][i] = num
                num += 1
            bottom -= 1

        if left <= right:
            for i in range(bottom, top - 1, -1):
                board[i][left] = num
                num += 1
            left += 1

    # Now check if rook pays less than queen
    # Simulate the path for rook and queen

    def simulate(piece):
        visited = [False] * (N * N)
        current = 0  # index of cell with number 1
        for i in range(N):
            for j in range(N):
                if board[i][j] == 1:
                    current = i * N + j
                    break
            else:
                continue
            break

        visited[current] = True
        fee = 0

        while True:
            min_num = None
            min_pos = -1
            for i in range(N):
                for j in range(N):
                    if not visited[i * N + j] and (board[i][j] == 1 or (piece == 'rook' and (i == current // N or j == current % N)) or (piece == 'queen' and (abs(i - current // N) == abs(j - current % N) or i == current // N or j == current % N))):
                        if min_num is None or board[i][j] < min_num:
                            min_num = board[i][j]
                            min_pos = i * N + j

            if min_pos == -1:
                break

            visited[min_pos] = True
            fee += 1
            current = min_pos

        return fee

    rook_fee = simulate('rook')
    queen_fee = simulate('queen')

    if rook_fee < queen_fee:
        for row in board:
            print(' '.join(map(str, row)))
