def main():
    import sys
    input = sys.stdin.read
    N = int(input())

    if N == 1:
        print(-1)
        return

    # For N >= 2, we can construct a board where rook pays less than queen
    # We use a spiral pattern to make the rook's path more efficient
    board = [[0]*N for _ in range(N)]
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]  # right, down, left, up
    dir_idx = 0
    x, y = 0, 0
    num = 1
    board[x][y] = num
    num += 1

    while num <= N*N:
        for _ in range(2):
            for _ in range(N):
                if num > N*N:
                    break
                nx, ny = x + directions[dir_idx][0], y + directions[dir_idx][1]
                if 0 <= nx < N and 0 <= ny < N and board[nx][ny] == 0:
                    x, y = nx, ny
                    board[x][y] = num
                    num += 1
            dir_idx = (dir_idx + 1) % 4

    # Now check if rook pays less than queen
    # We simulate both paths and count fees
    # This is a simplified simulation for the purpose of this problem
    # In practice, for large N, we would need a more efficient method

    def simulate(piece):
        visited = set()
        visited.add((0, 0))
        fee = 0
        current = (0, 0)
        while True:
            min_val = None
            min_pos = None
            for dx in [-1, 0, 1]:
                for dy in [-1, 0, 1]:
                    if dx == 0 and dy == 0:
                        continue
                    nx, ny = current[0] + dx, current[1] + dy
                    if 0 <= nx < N and 0 <= ny < N and board[nx][ny] != 0 and (nx, ny) not in visited:
                        if min_val is None or board[nx][ny] < min_val:
                            min_val = board[nx][ny]
                            min_pos = (nx, ny)
            if min_val is not None:
                visited.add(min_pos)
                fee += 1
                current = min_pos
            else:
                # Teleport to the smallest unvisited cell
                for i in range(N):
                    for j in range(N):
                        if board[i][j] != 0 and (i, j) not in visited:
                            min_val = board[i][j]
                            min_pos = (i, j)
                            break
                    if min_val is not None:
                        break
                if min_val is None:
                    break
                visited.add(min_pos)
                fee += 1
                current = min_pos
            if len(visited) == N*N:
                break
        return fee

    rook_fee = simulate('rook')
    queen_fee = simulate('queen')

    if rook_fee < queen_fee:
        for row in board:
            print(' '.join(map(str, row)))
    else:
        print(-1)

