    if n == 0:
        print(0)
        return

    max_inversions = n*(n-1)//2
    dp_size = 2 * max_inversions + 1
    dp = [[0] * dp_size for _ in range(n+1)]

    dp[0][max_inversions] = 1

    fact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % mod

    inv_fact = [1] * (n+1)
    inv_fact[n] = pow(fact[n], -1, mod) if mod != 1 else 0
    for i in range(n, 0, -1):
        inv_fact[i-1] = inv_fact[i] * i % mod

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % mod * inv_fact[n_val - r] % mod

    def nPr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[n_val - r] % mod

    for i in range(1, n+1):
        dp_next = [0] * dp_size
        for diff_idx in range(dp_size):
            if dp[i-1][diff_idx] == 0:
                continue
            for x in range(1, i+1):
                for y in range(1, i+1):
                    if x == y:
                        continue
                    sign_p = 1 if x < y else -1
                    sign_q = 1 if x < y else -1
                    new_diff = diff_idx + sign_p - sign_q
                    if new_diff < 0 or new_diff >= dp_size:
                        continue
                    cnt_x = nCr(i-1, x-1)
                    cnt_y = nPr(i-1, y-1)
                    add_val = dp[i-1][diff_idx] * cnt_x % mod * cnt_y % mod
                    dp_next[new_diff] = (dp_next[new_diff] + add_val) % mod
        dp[i] = dp_next

    total = 0
    for j in range(max_inversions + 1):
        total = (total + dp[n][j]) % mod

    print(total)

