    # Precompute inversion counts for all permutations of size n
    inv_count = [0] * (n + 1)
    for i in range(1, n + 1):
        inv_count[i] = inv_count[i - 1] + (i * (i - 1)) // 2

    # dp[i][j] = number of pairs (p, q) with len(p) = i, len(q) = j, and p < q, inv(p) > inv(q)
    dp = [[0] * (n + 1) for _ in range(n + 1)]

    # Base case: when i == j, no pairs since p and q are same length
    for i in range(1, n + 1):
        dp[i][i] = 0

    # Fill dp table
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            # For each possible inversion count in p and q
            for inv_p in range(0, inv_count[i] + 1):
                for inv_q in range(0, inv_count[j] + 1):
                    if inv_p > inv_q:
                        # Number of permutations of size i with inv_p inversions
                        # and j with inv_q inversions
                        # Total pairs is (count_p * count_q)
                        # But we need to compute it efficiently
                        pass

    # Since the above approach is too slow, we use a different approach using combinatorics and prefix sums
    # Let's precompute the number of permutations with exactly k inversions
    from collections import defaultdict

    # inv_count_perm[n] = number of permutations of size n with exactly k inversions
    inv_count_perm = [defaultdict(int) for _ in range(n + 1)]
    inv_count_perm[0][0] = 1

    for i in range(1, n + 1):
        for k in inv_count_perm[i - 1]:
            inv_count_perm[i][k] += inv_count_perm[i - 1][k]
            inv_count_perm[i][k + i - 1] += inv_count_perm[i - 1][k]

    # Now compute the answer
    ans = 0
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            for k in inv_count_perm[i]:
                for l in inv_count_perm[j]:
                    if k > l:
                        ans += inv_count_perm[i][k] * inv_count_perm[j][l]
                        ans %= mod

    print(ans % mod)

