def main():
    data = sys.stdin.read().split()
    n = int(data[0])
    k = int(data[1])
    a = list(map(int, data[2:2+n]))

    next_greater_left = [-1] * n
    stack = []
    for i in range(n):
        while stack and a[stack[-1]] < a[i]:
            stack.pop()
        if stack:
            next_greater_left[i] = stack[-1]
        else:
            next_greater_left[i] = -1
        stack.append(i)

    m = [[0]*(k+1) for _ in range(n+1)]
    dp = [[0]*(k+1) for _ in range(n+1)]

    for i in range(1, n+1):
        for j in range(1, k+1):
            best = 0
            cur_max = 0
            p = i-1
            while p >= 0:
                if a[p] > cur_max:
                    cur_max = a[p]
                if next_greater_left[p] == -1 or next_greater_left[p] < i-1 - j*(n//k) + 1:
                    seg_max = cur_max
                else:
                    seg_max = a[p]
                if p == i-1:
                    seg_max = a[p]
                if dp[p][j-1] + seg_max > best:
                    best = dp[p][j-1] + seg_max
