import math

def build_sparse_table(arr):
    n = len(arr)
    k = math.floor(math.log2(n)) + 1
    st = [[0] * n for _ in range(k)]
    for i in range(n):
        st[0][i] = arr[i]

    for j in range(1, k):
        for i in range(n - (1 << j) + 1):
            st[j][i] = max(st[j-1][i], st[j-1][i + (1 << (j-1))])

    return st, k

def rmq_query(st, logn, l, r):
    length = r - l + 1
    j = logn
    while (1 << j) > length:
        j -= 1
    return max(st[j][l], st[j][r - (1 << j) + 1])

def main():
    import sys
    data = sys.stdin.read().split()
    n = int(data[0])
    k = int(data[1])
    a = list(map(int, data[2:2+n]))

    if k == 1:
        print(sum(a))
        print(str(n))
        return

    st, logn_val = build_sparse_table(a)

    dp = [[-10**18] * (k+1) for _ in range(n+1)]
    for i in range(1, n+1):
        dp[i][1] = rmq_query(st, logn_val, 0, i-1)

    for days in range(2, k+1):
        for i in range(days, n+1):
            best = -10**18
            for p in range(days-1, i):
                seg_max = rmq_query(st, logn_val, p, i-1)
                prev_profit = dp[p][days-1]
                total_profit = prev_profit + seg_max
                if total_profit > best:
                    best = total_profit
            dp[i][days] = best

    result = []
    i = n
    day = k
    while day > 0:
        p = None
        best_profit = -10**18
        for t in range(day-1, 0, -1):
            for prev_i in range(t-1, i):
                seg_max = rmq_query(st, logn_val, prev_i, i-1)
                candidate = dp[prev_i][t-1] + seg_max
                if candidate == dp[i][day]:
                    p = prev_i
                    break
            if p is not None:
                break
        if p is None:
            for t in range(day-1, 0, -1):
                for prev_i in range(t-1, i):
                    seg_max = rmq_query(st, logn_val, prev_i, i-1)
                    candidate = dp[prev_i][t-1] + seg_max
                    if candidate == dp[i][day]:
                        p = prev_i
                        break
                if p is not None:
                    break
        i = p
        day -= 1
        result.append(i - p)

    total_problems = sum(result)
    assert total_problems == n, "The sum of the segments must be n"

    print(dp[n][k])
    print(" ".join(map(str, result)))

