def find_optimal_k(a, b):
    if a == b:
        return 0
    g = math.gcd(a, b)
    d = abs(a - b)
    # Find the largest divisor of d that divides g
    max_divisor = 0
    for i in range(1, int(math.isqrt(d)) + 1):
        if d % i == 0:
            if g % i == 0:
                max_divisor = i
            if g % (d // i) == 0:
                max_divisor = d // i
    k = (d - max_divisor) // 2
    return k

