def convert_base_a_to_base10(s, base):
    num = 0
    length = len(s)
    for i, char in enumerate(s):
        digit_val = char_to_digit(char)
        if digit_val >= base:
            raise ValueError(f"Invalid digit '{char}' in base {base}")
        num += digit_val * (base ** (length - i - 1))
    return num

def char_to_digit(c):
    if c in '0123456789':
        return int(c)
    else:
        return 10 + (ord(c) - ord('A'))

def convert_base10_to_base_b(n, base):
    if n == 0:
        return "0"

    digits = []
    while n:
        n, r = divmod(n, base)
        digits.append(digit_to_char(r))

    return ''.join(digits[::-1])

def digit_to_char(d):
    if d < 10:
        return str(d)
    else:
        return chr(ord('A') + d - 10)

def convert_to_roman(n):
    if n == 0:
        return ""
    roman_map = [
        ('M', 1000),
        ('CM', 900), ('D', 500), ('CD', 400),
        ('C', 100), ('XC', 90), ('L', 50), ('XL', 40),
        ('X', 10), ('IX', 9), ('V', 5), ('IV', 4),
        ('I', 1)
    ]
    roman = ''
    for numeral, value in roman_map:
        count = n // value
        roman += numeral * count
        n -= value * count
    return roman

