import sys
from collections import deque

def build_spf(n):
    spf = list(range(n + 1))
    for i in range(2, int(n ** 0.5) + 1):
        if spf[i] == i:
            for j in range(i * i, n + 1, i):
                if spf[j] == j:
                    spf[j] = i
    return spf

def get_prime_factors(x, spf):
    factors = {}
    while x > 1:
        p = spf[x]
        count = 0
        while x % p == 0:
            count += 1
            x //= p
        factors[p] = count
    return factors

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        print("YES")
        return

    it = iter(data)
    n = int(next(it)); m = int(next(it))

    edges = []
    for _ in range(m):
        u = int(next(it)); v = int(next(it)); g = int(next(it)); l = int(next(it))
        edges.append((u, v, g, l))

    spf_arr = build_spf(1000000)

    graph = [[] for _ in range(n + 1)]
    for u, v, g, l in edges:
        graph[u].append((v, g, l))
        graph[v].append((u, g, l))

    candidate = [0] * (n + 1)
    result = [0] * (n + 1)
    parent = [-1] * (n + 1)
    visited = [False] * (n + 1)

    def factorize_and_combine(a, b):
        total_factors = {}
        for num in [a, b]:
            fac = get_prime_factors(num, spf_arr)
            for p, exp in fac.items():
                total_factors[p] = max(total_factors.get(p, 0), exp)
        return total_factors

    def solve_component(start):
        q = deque([start])
        visited[start] = True
        candidate[start] = 1
        result[start] = 1

        while q:
            node = q.popleft()
            for neighbor, g_val, l_val in graph[node]:
                if visited[neighbor]:
                    continue
                visited[neighbor] = True
                parent[neighbor] = node
                candidate[neighbor] = 1

                a_prime = l_val // (g_val * candidate[node])
                if a_prime < 1:
                    return False

                factors_a = get_prime_factors(a_prime, spf_arr)
                if any(candidate[node] % p != 0 for p in factors_a):
                    return False

                factors_b = factorize_and_combine(candidate[node], a_prime)
                for p, exp in factors_b.items():
                    if candidate[neighbor] % p != exp:
                        candidate[neighbor] *= p ** exp

                result[neighbor] = candidate[neighbor] * g_val

                q.append(neighbor)

        return True

    for i in range(1, n + 1):
        if not visited[i]:
            if not solve_component(i):
                print("NO")
                return

    print("YES")
    for i in range(1, n + 1):
        print(result[i], end=' ')
    print()

