sys.setrecursionlimit(1000000)

def factorize(x):
    factors = {}
    d = 2
    while d * d <= x:
        while x % d == 0:
            factors[d] = factors.get(d, 0) + 1
            x //= d
        d += 1
    if x > 1:
        factors[x] = factors.get(x, 0) + 1
    return factors

def merge_factors(factors_list):
    merged = defaultdict(int)
    for d in factors_list[0].keys():
        merged[d] = max([factors_list[i].get(d, 0) for i in range(len(factors_list))])
    return merged

def lcm_from_gcd(g, l):
    if l % g != 0:
        return None
    total_product = g * l
    return total_product

