        index += 4
        edges.append((u, v, g_val, l_val))
        graph[u].append((v, g_val, l_val))
        graph[v].append((u, g_val, l_val))

    for (u, v, g_val, l_val) in edges:
        if l_val % g_val != 0:
            print("NO")
            return

    candidate_ans = [0] * (n+1)
    candidate_ans[0] = 1
    for i in range(1, n+1):
        candidate_ans[i] = 0

    for i in range(1, n+1):
        if candidate_ans[i] != 0:
            continue
        stack = [i]
        candidate_ans[i] = 1
        factors_dict = defaultdict(dict)
        factorized_edges = []
        for neighbor, g_val, l_val in graph[i]:
            total_product = g_val * l_val
            prod_factors = factorize(total_product)
            factors_dict[neighbor] = prod_factors
            factorized_edges.append(prod_factors)

        merged_factors = merge_factors(factorized_edges)
        base = 1
        for p, exp in merged_factors.items():
            base *= pow(p, exp)

        candidate_ans[i] = base

        while stack:
            node = stack.pop()
            for neighbor, g_val, l_val in graph[node]:
                if candidate_ans[neighbor] == 0:
                    total_product = g_val * l_val
                    prod_factors = factorize(total_product)
                    factors_dict[neighbor] = prod_factors
                    factorized_edges.append(prod_factors)
                    merged_factors = merge_factors([factors_dict[j] for j in graph[node]])
                    new_base = 1
                    for p, exp in merged_factors.items():
                        new_base *= pow(p, exp)
                    candidate_ans[neighbor] = new_base
                    stack.append(neighbor)

    valid = True
    for (u, v, g_val, l_val) in edges:
        a = candidate_ans[u]
        b = candidate_ans[v]
        if a == 0 or b == 0:
