            break
        if a * b != g_val * l_val:
            valid = False
            break
        if gcd(a, b) != g_val or lcm(a, b) != l_val:
            valid = False
            break

    if not valid:
        print("NO")
        return

    print("YES")
    print(" ".join(str(x) for x in candidate_ans[1:]))

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
