import sys
from collections import defaultdict, deque

def build_spf(n):
    spf = list(range(n+1))
    for i in range(2, int(n**0.5)+1):
        if spf[i] == i:
            for j in range(i*i, n+1, i):
                if spf[j] == j:
                    spf[j] = i
    return spf

def factorize_with_spf(x, spf):
    factors = {}
    while x > 1:
        p = spf[x]
        cnt = 0
        while spf[x] == p:
            cnt += 1
            x //= p
        factors[p] = cnt
    return factors

def main():
    data = sys.stdin.read().split()
    if not data:
        print("NO")
        return

    it = iter(data)
    n = int(next(it)); m = int(next(it))

