    for _ in range(m):
        u = int(next(it)) - 1
        v = int(next(it)) - 1
        g_val = int(next(it))
        l_val = int(next(it))
        edges.append((u, v, g_val, l_val))

    max_val = 10**6
    spf_arr = build_spf(max_val)

    valid_edges = True
    for u, v, g_val, l_val in edges:
        if g_val > l_val:
            valid_edges = False
            break
        if l_val % g_val != 0:
            valid_edges = False
            break

    if not valid_edges:
        print("NO")
        return

    all_numbers = set()
    for u, v, g_val, l_val in edges:
        all_numbers.add(g_val)
        all_numbers.add(l_val)

    primes_set = set()
    for num in all_numbers:
        temp = num
        while temp > 1:
            p = spf_arr[temp]
            primes_set.add(p)
            while temp % p == 0:
                temp //= p

    prime_edges = defaultdict(list)
    for (u, v, g_val, l_val) in edges:
        factors_g = factorize_with_spf(g_val, spf_arr)
        factors_l = factorize_with_spf(l_val, spf_arr)
        for p in set(factors_g.keys()) | set(factors_l.keys()):
            if p not in factors_g:
                exp_g = 0
            else:
                exp_g = factors_g[p]
            if p not in factors_l:
                exp_l = 0
            else:
                exp_l = factors_l[p]
            if exp_g > exp_l // 2:
                print("NO")
                return
            prime_edges[p].append((u, v, exp_g, exp_l))

    node_exponents = {}
    for p, edges_p in prime_edges.items():
        graph = defaultdict(list)
        node_set = set()
        for (u, v, exp_g, exp_l) in edges_p:
            node_set.add(u)
            node_set.add(v)
            if exp_g == exp_l - exp_g:
                graph[u].append((v, 'same'))
                graph[v].append((u, 'same'))
            else:
                graph[u].append((v, 'diff'))
                graph[v].append((u, 'diff'))

        comp_nodes = defaultdict(list)
        visited = set()
        for node in node_set:
            if node not in visited:
                stack = [node]
                visited.add(node)
                while stack:
                    cur = stack.pop()
                    for neighbor, typ in graph[cur]:
                        if neighbor not in visited:
                            visited.add(neighbor)
                            stack.append(neighbor)
                comp_nodes[node].extend(visited - {node})

        for comp in comp_nodes.values():
            color_map = {}
            q = deque()
            start_node = comp[0]
            color_map[start_node] = 0
            q.append(start_node)
            while q:
                node = q.popleft()
                for neighbor, typ in graph[node]:
                    if typ == 'same':
                        if neighbor not in color_map:
                            color_map[neighbor] = color_map[node]
                            q.append(neighbor)
                        elif color_map[neighbor] != color_map[node]:
                            print("NO")
                            return
                    else:
                        if neighbor not in color_map:
                            color_map[neighbor] = 1 - color_map[node]
                            q.append(neighbor)
                        elif color_map[neighbor] == color_map[node]:
                            print("NO")
                            return
            for node in comp:
                if node not in node_exponents:
                    node_exponents[node] = {}
                node_exponents[node][p] = 0 if color_map.get(node, 0) == 0 else (exp_l - exp_g)

    ans = [1] * n
    for p, edges_p in prime_edges.items():
        for (u, v, exp_g, exp_l) in edges_p:
            if u in node_exponents and p in node_exponents[u]:
                exp_u = node_exponents[u][p]
            else:
                exp_u = 0
            if v in node_exponents and p in node_exponents[v]:
                exp_v = node_exponents[v][p]
            else:
                exp_v = 0
            if u < n:
                ans[u] *= (p ** exp_u)
            if v < n:
                ans[v] *= (p ** exp_v)

    for i in range(n):
        while ans[i] < 1:
            ans[i] = 1

    print("YES")
    print(" ".join(map(str, ans)))

