import sys

def precompute_factorials(max_n, mod=MOD):
    fact = [1] * (max_n + 1)
    inv_fact = [1] * (max_n + 1)
    for i in range(2, max_n + 1):
        fact[i] = fact[i - 1] * i % mod
    inv_fact[max_n] = pow(fact[max_n], mod - 2, mod)
    for i in range(max_n, 0, -1):
        inv_fact[i - 1] = inv_fact[i] * i % mod
    return fact, inv_fact

def main():
    data = sys.stdin.read().split()
    if not data:
        return
    n = int(data[0])

    if n == 1:
        print(0)
        return

    if n % 2 != 0:
        print(0)
        return

    max_n = 500000
    fact, inv_fact = precompute_factorials(max_n, MOD)

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % MOD * inv_fact[n_val - r] % MOD

    def nHr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val + r - 1] * inv_fact[r] % MOD * inv_fact[n_val - 1] % MOD

    def nPr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[n_val - r] % MOD

    def power_mod(base, exp, mod=MOD):
        result = 1
        base %= mod
        while exp:
            if exp & 1:
                result = (result * base) % mod
            base = (base * base) % mod
            exp >>= 1
        return result

    def T(n_val):
        if n_val <= 1:
            return 1
        return nCr(n_val - 1, n_val // 2)

    def U(n_val):
        if n_val <= 1:
            return 0
        k = n_val - 1
        res = 0
        for r in range(1, k + 1):
            term1 = nCr(k, r)
            term2 = power_mod(r, k, MOD)
            res = (res + term1 * term2) % MOD
        return res

    if n == 2:
        print(-1 % MOD)
        return

    ans = 0
    m = n - 1
    k = n // 2

    part1 = power_mod(m, n - 2, MOD) * power_mod(2, m, MOD) % MOD
    part2 = T(n) * power_mod(2, m - 1, MOD) % MOD
    part3 = U(n) * power_mod(2, m - 1, MOD) % MOD

    ans = (part1 + part2 - part3) % MOD
    print(ans % MOD)

