def min_ternary_string(s):
    s = list(s)
    n = len(s)

    # Count the number of each character
    count = {'0': 0, '1': 0, '2': 0}
    for c in s:
        count[c] += 1

    # We can only swap '0' and '1', and '1' and '2'
    # So '0's can move past '1's, '1's can move past '2's, but not '0's past '2's

    # First, place all '0's at the beginning
    idx = 0
    for i in range(n):
        if s[i] == '0':
            s[idx], s[i] = s[i], s[idx]
            idx += 1

    # Now, place '1's as close to the left as possible among the remaining positions
    # But '1's can only move past '2's, so we need to count how many '1's are after '2's
    # and adjust accordingly

    # Count the number of '1's that are after '2's
    ones_after_twos = 0
    for i in range(n):
        if s[i] == '1':
            for j in range(i + 1, n):
                if s[j] == '2':
                    ones_after_twos += 1
                else:
                    break

    # Now, we can move these '1's to the left by swapping with '2's
    # We need to shift the '1's left by the number of '2's they have passed
    # So we will simulate this by moving '1's left as much as possible

    # Start from the end and move '1's left as much as possible
    i = n - 1
    while i >= 0:
        if s[i] == '1':
            # Move this '1' as far left as possible
            j = i
            while j > 0 and s[j - 1] == '2':
                s[j], s[j - 1] = s[j - 1], s[j]
                j -= 1
        i -= 1

    return ''.join(s)

