class Node:
    __slots__ = ['children', 'fail', 'valid']
    def __init__(self):
        self.children = {}
        self.fail = None
        self.valid = False

def build_automaton(patterns):
    root = Node()
    for pattern in patterns:
        node = root
        for char in pattern:
            if char not in node.children:
                node.children[char] = Node()
            node = node.children[char]
        node.valid = True

    from collections import deque
    q = deque()
    root.fail = root
    for char, child in root.children.items():
        child.fail = root
        q.append(child)

    while q:
        curr = q.popleft()
        for char, child in curr.children.items():
            fail_node = curr.fail
            while fail_node != root and char not in fail_node.children:
                fail_node = fail_node.fail
            child.fail = fail_node.children.get(char, root)
            child.valid |= child.fail.valid
            q.append(child)

    return root

