        # Update DP table
        new_dp = {}
        for prev_len in range(1, 5):
            if i - prev_len + 1 < 0:
                continue
            current = ''.join(S[-prev_len:])
            if current in morse_to_idx:
                idx = morse_to_idx[current]
                if prev_len == 1:
                    new_dp[idx] = 1
                else:
                    for key in dp[i - prev_len + 1]:
                        new_dp[key] = (new_dp.get(key, 0) + dp[i - prev_len + 1][key]) % MOD
