    B_str = input().strip()

    L1 = len(A_str)
    L2 = len(B_str)

    g = math.gcd(L1, L2)
    T = L1 * L2 // g

    def outcome(a, b):
        if a == b:
            return None
        if (a, b) in [('R', 'S'), ('S', 'P'), ('P', 'R')]:
            return 'P'
        else:
            return 'N'

    total_N_cycle = 0
    total_P_cycle = 0

    for x in range(L1):
        r1 = x % g
        for y in range(L2):
            if y % g == r1:
                res = outcome(A_str[x], B_str[y])
                if res == 'N':
                    total_N_cycle += 1
                elif res == 'P':
                    total_P_cycle += 1

    cycles = n // T
    rem = n % T

    total_red_N = cycles * total_N_cycle
    total_red_P = cycles * total_P_cycle

    for r in range(rem):
        x_index = r % L1
        y_index = r % L2
        a_char = A_str[x_index]
        b_char = B_str[y_index]
        res = outcome(a_char, b_char)
        if res == 'N':
            total_red_N += 1
        elif res == 'P':
            total_red_P += 1

    print(f"{total_red_N} {total_red_P}")

