                    output_lines.append(f"{j} {idx}")
                    matched_left.add(j)
                    break

    for i in range(W_u):
        if i+1 in left_wild:
            if m3 > 0:
                m3 -= 1
                for j in right_wild:
                    if j not in matched_right and (i+1) not in matched_left:
                        output_lines.append(f"{i+1} {j}")
