        duration_map[dur].append((l, r, cost_val))

    ans = float('inf')
    considered = set()

    for dur in list(duration_map.keys()):
        dur2 = x - dur
        if dur2 < 0:
            continue

        key1 = (dur, dur2)
        key2 = (dur2, dur)
        if key1 in considered or key2 in considered:
            continue
        considered.add(key1)
        considered.add(key2)

        if dur == dur2:
            A = duration_map[dur]
            A.sort(key=lambda x: x[1])
            nA = len(A)
            min_prefix = [0] * nA
            min_prefix[0] = A[0][2]
            for i in range(1, nA):
                min_prefix[i] = min(min_prefix[i-1], A[i][2])

            A_sorted_by_l = sorted(A, key=lambda x: x[0])
            min_suffix = [0] * nA
            min_suffix[-1] = A_sorted_by_l[-1][2]
