def is_perfect_square(x):
    if x < 0:
        return False
    s = math.isqrt(x)
    return s * s == x

def solve_case_two_levels(n):
    discriminant = 4 * n - 3
    if not is_perfect_square(discriminant):
        return False
    root = math.isqrt(discriminant)
    k = (-1 + root) / 2
    if k < 2 or k != int(k):
        return False
    return True

def solve_case_more_levels(n):
    max_exponent = 60
    found = False
    for exponent in range(4, max_exponent + 1):
        low = 2
        high = int(round(n**(1/(exponent - 1)))) + 2
        if high < low:
            continue
        while low <= high:
            mid = (low + high) // 2
            power_val = pow(mid, exponent)
            right_side = n * (mid - 1) + 1
            if power_val == right_side:
                return True
            elif power_val < right_side:
                low = mid + 1
            else:
                high = mid - 1
    return False

