from collections import deque

def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    test_cases = []
    for line in data:
        if line.strip():
            test_cases.append(line.strip())

    results = []
    for s in test_cases:
        if s == "":
            continue

        grid = []
        n = len(s)
        width = 80
        rows = (n + width - 1) // width

        for i in range(rows):
            start = i * width
            end = start + width
            if end > n:
                end = n
                grid.append(s[start:end] + ' '*(width - (end - start)))
            else:
                grid.append(s[start:end])

        if not grid:
            results.append("false")
            continue

        stack = []
        pos = [0, 0]
        visited = set()
        constraints = []
        variables = {}
        dir_stack = []
        current_direction = [0, 1]

        def push_digit(c):
            nonlocal stack
            num = int(c)
            stack.append(num)

        def process_instruction(r, c):
            ch = grid[r][c]
            if ch == ' ':
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch in '0123456789':
                push_digit(ch)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '~':
                if stack:
                    stack.pop()
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '.':
                if stack:
                    # Output the top value, but we don't care about output in our simulation for constraints
                    pass
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == ',':
                if stack:
                    val = stack.pop()
                    constraints.append(('digit', val))
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '#':
                if stack:
                    r1 = stack.pop()
                    c1 = stack.pop()
                    new_r = r + r1
                    new_c = c + c1
                    if new_r >= 0 and new_r < len(grid) and new_c >= 0 and new_c < len(grid[0]):
                        pos[0] = new_r
                        pos[1] = new_c
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == ':':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a)
                    stack.append(b)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '!':
                if stack:
                    a = stack.pop()
                    if a != 0:
                        stack.append(1)
                    else:
                        stack.append(0)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '$':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '%':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append((b * 10) + a)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '&':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(b - a)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '(':
                if len(stack) >= 1:
                    a = stack.pop()
                    stack.append(a)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == ')':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a)
                    stack.append(b)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '*':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a * b)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '+':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a + b)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '-':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(b - a)
                elif len(stack) == 1:
                    a = stack.pop()
                    stack.append(-a)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '.':
                if stack:
                    pass
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '/':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(int(b / a))
                return True, pos, constraints, dir_stack, current_direction

            if ch == '0' and len(stack) >= 2:
                a = stack.pop()
                b = stack.pop()
                stack.append(0)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '0' and len(stack) == 1:
                a = stack.pop()
                stack.append(0)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '1' and len(stack) >= 2:
                a = stack.pop()
                b = stack.pop()
                stack.append(1)
                return True, pos, stack, constraints, dir_stack, current_direction

            if ch == '1' and len(stack) == 1:
                a = stack.pop()
