                while i < len(s):
                    if s[i] == '_':
                        j = i + 1
                        while j < len(s) and s[j] == '_':
                            j += 1
                        tokens.append(('var', s[i:i+(j-i)]))
                        i = j
                    else:
                        tokens.append(('op', s[i]))
                        i += 1
                return tokens

            expr_tokens = parse_tokens(expr_str)
            cond_tokens = parse_tokens(cond_str)

            ops = {
                '+': lambda a, b: a + b,
                '-': lambda a, b: a - b,
