def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    results = []

    for s in data:
        s = s.strip()
        if not s:
            continue

        grid = []
        current_line = ""
        for char in s:
            if char == '\n':
                if current_line:
                    grid.append(current_line)
                    current_line = ""
            else:
                current_line += char
        if current_line:
            grid.append(current_line)

        if not grid:
            results.append("false")
            continue

        n_rows = len(grid)
        n_cols = max(len(row) for row in grid) if n_rows > 0 else 0

        if n_rows == 0 or n_cols == 0:
            results.append("false")
            continue

        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        stack = []
        visited = [[set() for _ in range(n_cols)] for _ in range(n_rows)]
        paths = []
        start_r, start_c = 0, 0

        def get_char(r, c):
            if 0 <= r < n_rows and 0 <= c < len(grid[r]):
                return grid[r][c]
            return ' '

        def get_next_pos(r, c, dr, dc):
            cmd = get_char(r, c)
            if cmd == '>':
                new_dir = directions[(directions.index((dr, dc)) + 1) % 4]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == '<':
                new_dir = directions[(directions.index((dr, dc)) - 1) % 4]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == 'v':
                new_dir = directions[(directions.index((dr, dc)) + 2) % 4]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == '^':
                new_dir = directions[(directions.index((dr, dc)) + 3) % 4]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == '_':
                if dr == 1:  # Moving down
                    new_dir = directions[3]
                else:  # Moving up
                    new_dir = directions[0]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == '|':
                if dr == 1:  # Moving down
                    new_dir = directions[2]
                else:  # Moving up
                    new_dir = directions[1]
                return r, c, new_dir[0], new_dir[1], False
            elif cmd == '$':
                if stack:
                    visited[r][c].add(tuple(stack[-1]))
                return r, c, dr, dc, True
            elif cmd in '0123456789':
                num = int(cmd)
                stack.append(num)
                return r, c, dr, dc, True
            elif cmd == '.' or cmd == ',' or cmd == ':' or cmd == '#':
                return r, c, dr, dc, True
            elif cmd == '(' or cmd == ')':
                return r, c, dr, dc, True
            elif cmd == '+' or cmd == '-' or cmd == '*' or cmd == '/' or cmd == '%' or cmd == '&' or cmd == '\\' or cmd == '~':
                if len(stack) >= 2:
                    a = stack.pop()
                    b = stack.pop()
                    if cmd == '+':
                        stack.append(a + b)
                    elif cmd == '-':
                        stack.append(b - a)
                    elif cmd == '*':
                        stack.append(a * b)
                    elif cmd == '/':
                        if a != 0:
                            stack.append(b // a)
                        else:
                            stack.append(0)
                    elif cmd == '%':
                        if a != 0:
                            stack.append(b % a)
                        else:
                            stack.append(0)
                    elif cmd == '&':
                        pass
                    elif cmd == '\\':
                        stack.append(a)
                        stack.append(b)
                    elif cmd == '~':
                        pass
                return r, c, dr, dc, True
            else:
                return r, c, dr, dc, True

        def dfs(r, c, dr, dc, seq, stack_copy):
            if (r, c) in visited[r][c]:
                if tuple(stack_copy) in visited[r][c]:
                    return
                else:
                    visited[r][c].add(tuple(stack_copy))
            else:
                visited[r][c].add(tuple(stack_copy))

            if (r, c) == (start_r, start_c) and dr == 0 and dc == 1 and seq == "":
                if not paths:
                    paths.append(seq)
                else:
                    if seq < paths[0]:
                        paths.clear()
                        paths.append(seq)
                    elif paths[0] == "" or seq < paths[0]:
                        paths.clear()
                        paths.append(seq)
                return

            cmd = get_char(r, c)
            if cmd == '>':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == '<':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == 'v':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == '^':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == '_':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == '|':
                next_r, next_c, next_dr, next_dc, is_stack_altered = get_next_pos(r, c, dr, dc)
                dfs(next_r, next_c, next_dr, next_dc, seq, stack_copy)
            elif cmd == '$':
                if stack_copy:
                    digit_val = stack_copy.pop()
                    dfs(r, c, dr, dc, seq + str(digit_val), stack_copy[:])
                else:
                    dfs(r, c, dr, dc, seq, stack_copy)
            elif cmd in '0123456789':
                num = int(cmd)
                dfs(r, c, dr, dc, seq + str(num), stack_copy + [num])
            elif cmd == '.' or cmd == ',' or cmd == ':' or cmd == '#':
                dfs(r, c, dr, dc, seq, stack_copy)
            elif cmd == '(' or cmd == ')':
                dfs(r, c, dr, dc, seq, stack_copy)
            elif cmd == '+' or cmd == '-' or cmd == '*' or cmd == '/' or cmd == '%' or cmd == '&' or cmd == '\\' or cmd == '~':
                if cmd == '&':
                    pass
                elif len(stack_copy) >= 2:
                    a = stack_copy.pop()
                    b = stack_copy.pop()
                    if cmd == '+':
                        stack_copy.append(a + b)
                    elif cmd == '-':
                        stack_copy.append(b - a)
                    elif cmd == '*':
                        stack_copy.append(a * b)
                    elif cmd == '/':
                        if a != 0:
                            stack_copy.append(b // a)
                        else:
                            stack_copy.append(0)
                    elif cmd == '%':
                        if a != 0:
                            stack_copy.append(b % a)
                        else:
                            stack_copy.append(0)
                    elif cmd == '\\':
                        stack_copy.append(a)
                        stack_copy.append(b)
                    elif cmd == '~':
                        pass
                dfs(r, c, dr, dc, seq, stack_copy)
            else:
                dfs(r, c, dr, dc, seq, stack_copy)

        stack_initial = []
        visited = [[set() for _ in range(n_cols)] for _ in range(n_rows)]
        paths = []
        dfs(start_r, start_c, 0, 1, "", stack_initial)

        if not paths:
