import sys
from collections import deque

def tokenize(program):
    tokens = []
    i = 0
    n = len(program)
    while i < n:
        if program[i] == '_':
            j = i
            while j < n and program[j] == '_':
                j += 1
            tokens.append(('VAR', j - i))
            i = j
        else:
            tokens.append(('OP', program[i]))
            i += 1
    return tokens

def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return
    results = []
    for line in data:
        program_str = line.strip()
        tokens = tokenize(program_str)
        var_count = sum(1 for token in tokens if token[0] == 'VAR')

        if var_count == 0:
            results.append("false")
            continue

        stack = []
        constraints = []
        current_constraint = []
        in_condition = False

        for token in tokens:
            if token[0] == 'OP':
                op = token[1]
                if op == '(':
                    in_condition = True
                    current_constraint.append(op)
                elif op == ')':
                    if in_condition:
                        in_condition = False
                    current_constraint.append(op)
                elif op == '?':
                    if in_condition:
                        constraints.append(''.join(current_constraint))
                        current_constraint = []
                    else:
                        current_constraint.append(op)
                elif op == ':':
                    if in_condition:
                        constraints.append(''.join(current_constraint))
                        current_constraint = []
                    else:
                        current_constraint.append(op)
                else:
                    current_constraint.append(op)
            else:
                current_constraint.append(token[1] * token[1].count('_'))

        if current_constraint:
            constraints.append(''.join(current_constraint))

        if not constraints:
            results.append("false")
            continue

        def backtrack(idx, current_solution):
            if idx == var_count:
                return current_solution

            for digit in range(0, 10):
                new_solution = current_solution + [digit]
                valid = True
                for constraint in constraints:
                    if not eval_constraint(constraint, new_solution):
                        valid = False
                        break
                if valid:
                    result = backtrack(idx + 1, new_solution)
                    if result is not None:
                        return result
            return None

        solution = backtrack(0, [])
        if solution is None:
            results.append("false")
        else:
            results.append(''.join(map(str, solution)))

    for res in results:
        print(res)

def eval_constraint(constraint, solution):
    var_map = {}
    var_names = ['v' + str(i) for i in range(len(solution))]
    for i, digit in enumerate(solution):
        var_map['v' + str(i)] = digit

    try:
        exec(f"result = {constraint.replace('VAR', 'var')}", {'result': None, 'var' + str(i): digit for i, digit in enumerate(solution)}, var_map)
        return var_map['result']
    except Exception as e:
        return False

