import sys
import re

def parse_golorp(name):
    # Extract the pattern from the name
    pattern = re.search(r'\?([^:]+):', name).group(1)

    # Split into jaws and stomach parts
    jaws_part, stomach_part = pattern.split(':')

    # Parse jaws: each jaw is a sequence of operators and variables
    jaws = []
    current_jaw = []
    for c in jaws_part:
        if c == '_':
            current_jaw.append('_')
        elif c == '*':
            jaws.append(''.join(current_jaw))
            current_jaw = ['*']
        elif c == '-':
            jaws.append(''.join(current_jaw))
            current_jaw = ['-']
        elif c == '+':
            jaws.append(''.join(current_jaw))
            current_jaw = ['+']
        elif c == '(' or c == ')':
            jaws.append(''.join(current_jaw))
            current_jaw = [c]
        else:
            current_jaw.append(c)
    if current_jaw:
        jaws.append(''.join(current_jaw))

    # Parse stomach: each condition is a comparison between two variables
    conditions = []
    left = None
    for c in stomach_part:
        if c == '_':
            if left is None:
                left = ''
            else:
                left += c
        elif c == '<':
            if left is not None:
                conditions.append((left, ''))
                left = None
        elif c == '>':
            if left is not None:
                conditions.append((left, ''))
                left = None
        elif c == ',':
            if left is not None:
                conditions.append((left, ''))
                left = None
        else:
            if left is not None:
                left += c
    if left is not None:
        conditions.append((left, ''))

    return jaws, conditions

def is_valid_sequence(jaws, sequence):
    # Check if the sequence satisfies all conditions
    for i in range(len(conditions)):
        left_var, right_var = conditions[i]
        left_val = int(sequence[int(left_var) - 1]) if left_var else 0
        right_val = int(sequence[int(right_var) - 1]) if right_var else 0

        if left_var and right_var:
            if conditions[i][0] == '>' and left_val <= right_val:
                return False
            elif conditions[i][0] == '<' and left_val >= right_val:
                return False
        elif left_var:
            if conditions[i][0] == '>' and left_val <= 0:
                return False
            elif conditions[i][0] == '<' and left_val >= 0:
                return False
        elif right_var:
            if conditions[i][0] == '>' and 0 <= 0:
                return False
            elif conditions[i][0] == '<' and 0 >= 0:
                return False
    return True

def find_lex_smallest_sequence(jaws, conditions):
    # Generate all possible sequences of length equal to the number of jaws
    n = len(jaws)
    min_seq = None

    # Try all possible sequences of digits 0-9
    for seq in product('0123456789', repeat=n):
        if is_valid_sequence(jaws, ''.join(seq)):
            if min_seq is None or ''.join(seq) < min_seq:
                min_seq = ''.join(seq)

    return min_seq if min_seq is not None else 'false'

def main():
    input_str = sys.stdin.read().strip()
    jaws, conditions = parse_golorp(input_str)
    result = find_lex_smallest_sequence(jaws, conditions)
    print(result)

