    low2 = L + 1
    high2 = 5 * (m + 1) + 1

    while low2 < high2:
        mid2 = (low2 + high2) // 2
        if count_trailing_zeros(mid2) < m + 1:
            low2 = mid2 + 1
        else:
            high2 = mid2

    R = low2

    result_list = list(range(L, R))

    print(str(len(result_list)))
    if result_list:
        print(" ".join(map(str, result_list)))
    else:
        print()

def count_trailing_zeros(n):
    count = 0
    divisor = 5
    while divisor <= n:
        count += n // divisor
        divisor *= 5
    return count

