def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0])
    s = data[1].strip()

    total_teams = 1 << n
    winners_required = total_teams

    res = set()

    def simulate(pos):
        left_bound = 1
        right_bound = total_teams
        current_phase = n - 1

        while current_phase >= 0:
            if winners_required == 1:
                break

            mid = (left_bound + right_bound) // 2
            if s[current_phase] == '1':
                if pos % 2 == 0:
                    right_bound = mid - 1
                else:
                    left_bound = mid
            else:
                if pos % 2 == 0:
                    left_bound = mid + 1
                else:
                    right_bound = mid

            current_phase -= 1
            winners_required //= 2

        return (left_bound <= right_bound)

    for pos in range(total_teams):
        if simulate(pos):
            res.add(pos + 1)

    print(" ".join(map(str, sorted(res))))

