def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0])
    s = data[1].strip()

    total_teams = 1 << n
    winners = [True] * total_teams

    def simulate_round(phase_index, current_winners):
        if phase_index == len(s):
            return current_winners

        num_games = 1 << (n - 1 - phase_index)
        next_winners = []

        for i in range(0, len(current_winners), 2):
            if i + 1 >= len(current_winners):
                next_winners.append(current_winners[i])
            else:
                a = current_winners[i]
                b = current_winners[i+1]
                if s[phase_index] == '1':
                    next_winners.append(max(a, b))
                else:
                    next_winners.append(min(a, b))
        return simulate_round(phase_index + 1, next_winners)

    for seed in range(1, total_teams + 1):
        winners = [seed]
        final_champion = simulate_round(0, [seed])
        if final_champion:
            winners[seed-1] = seed

    result_set = set()
    for idx, val in enumerate(winners):
        if val is not None and val > 0:
            result_set.add(val)

    if not result_set:
        print("")
    else:
        sorted_result = sorted(result_set)
        print(" ".join(map(str, sorted_result)))

