    main()

def main():
    n = int(input().strip())
    s_str = input().strip()

    total_teams = 2 ** n

    req_A = 0
    req_B = 0

    for i in range(n):
        size = 1 << (n - i - 1)  # 2^(n-i-1)
        if s_str[i] == '1':
            req_A += size
        else:
            req_B += size

    result = []
    for x in range(1, total_teams + 1):
        if req_A <= x - 1 and req_B <= total_teams - x:
            result.append(str(x))

    print(" ".join(result))

