def can_win(x, s, n):
    total_rounds = n
    num_teams = 1 << n

    if x == num_teams:
        return True

    min_required = 0
    max_required = num_teams + 1

    for i in range(total_rounds):
        num_games_in_round = 1 << (total_rounds - i - 1)
        round_rule = s[i]

        if round_rule == '1':
            min_required += 1
