        p = [0] * (total_teams + 1)
        for i in range(1, total_teams + 1):
            p[i] = i

        def solve(arr, s_remaining):
            if len(arr) == 1:
                return p[arr[0]] == x

            games = len(arr) // 2
            for _ in range(games):
                a = arr.pop()
                b = arr.pop()
                if s_remaining[0] == '0':
                    winner = min(p[a], p[b])
                else:
                    winner = max(p[a], p[b])
                arr.append(winner)
            s_remaining = s_remaining[1:]
            return solve(arr, s_remaining)

        for attempt in range(total_teams + 1):
            p[x] = attempt
            for i in range(1, total_teams + 1):
                if i != x:
                    p[i] = (attempt + 1 + i - 1) % total_teams
                    if p[i] < x:
                        break
            else:
                if solve([i for i in range(1, total_teams + 1)], s):
                    return True
        return False

    winners = []
    for x in range(1, total_teams + 1):
